    public static Mapper getMapper(int year) {
        switch (year) {
            case 2017: return Mapper2017.getMapper();
            case 2018: return Mapper2018.getMapper();
            default: throw new UnsupportedOperationException("Invalid year: " + year);
        }
    }

    private static void populate(String json, Map<Integer, String> map) {
        JsonArray arr = new JsonParser().parse(json).getAsJsonArray();
        for(JsonElement e : arr)
            map.put(Integer.parseInt(e.getAsJsonObject().get("id").getAsString()), e.getAsJsonObject().get("naziv").getAsString());
    }

